import React, { Component } from "react";
import WantToRead from "./wantToRead";
import CurrentlyReading from "./currentlyReading";
import Read from "./read";
import * as BooksAPI from "../BooksAPI";

class main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: {}
    };
  }

  //This will load all the books from BooksAPI
  componentDidMount() {
    BooksAPI.getAll().then(book => {
      this.state.books = book;
      this.setState(this.state.books);
    });
  }
  render() {
    return (
      <div id="main">
        <WantToRead {...this.state} bookList={this.state.books} />
        <CurrentlyReading {...this.state} bookList={this.state.books} />
        <Read {...this.state} bookList={this.state.books} />
      </div>
    );
  }
}

export default main;
