import React, { Component } from "react";
import * as BooksAPI from "../BooksAPI";

class wantToRead extends Component {
  constructor(props) {
    super(props);

    //this holds the status of display for each book
    this.state = { isToggleOn: false };

    this.toggleMenu = this.toggleMenu.bind(this);
  }

  //This will toggle the menu when the button is clicked
  toggleMenu = e => {
    if (e.target.id) {
      this.setState(state => ({
        isToggleOn: !state.isToggleOn
      }));
    }
  };

  //This creates books based on the location of the books
  displayBooks = () => {
    return this.props.bookList.map(item => {
      if (item.shelf === "wantToRead") {
        return (
          <div id={item.id} id="book">
            <img
              id={item.id}
              src={item.imageLinks.smallThumbnail}
              height="100"
              width="80"
              location={item.location}
              alt={item.authors}
              onClick={e => this.toggleMenu(e)}
            />
            <form
              id="locationControl"
              style={{ display: this.state.isToggleOn ? "block" : "none" }}
            >
              <button type="button">Want To Read</button>
              <button type="button">Currently Reading</button>
              <button type="button">Read</button>
              <button type="button">None</button>
            </form>
          </div>
        );
      }
    });
  };

  render = () => {
    return (
      <div className="wantToRead">
        <h2 className="heading">Want to Read</h2>
        <hr className="style-two" />
        <div className="wanttoreadcontainer" id="book" />
        {this.displayBooks()}
      </div>
    );
  };
}

export default wantToRead;
