import React, { Component } from "react";
import { Link } from "react-router-dom";
import { withRouter } from "react-router-dom";

class header extends Component {
  state = { input: "" };

  //This will get the value of the search term and input it into the state
  handleChange = e => {
    this.setState({ input: e.target.value });
  };

  //This will allow the user to press enter to submit the search term
  submit = e => {
    e.preventDefault();
    this.props.history.push({
      pathname: "/search",
      search: "?search=" + this.state.input
    });
    window.location.reload();
  };

  render() {
    return (
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark" id="nav">
        <a className="navbar-brand">
          <h1 className="titleheading">My Bookreads</h1>
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <form
            className="form-inline my-2 my-lg-0"
            onSubmit={this.handleChange}
            //onChange={this.handleChange}
          >
            <input
              className="form-control mr-sm-2"
              type="search"
              placeholder="Search"
              aria-label="Search"
              onChange={this.handleChange}
            />
            <Link
              to={{
                pathname: "/search",
                search: "?search=" + this.state.input
              }}
            >
              <button
                type="button"
                className="btn btn-outline-success my-2 my-sm-0"
              >
                Search
              </button>
            </Link>
          </form>
        </div>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to={"/"}> Home</Link>
            </li>
            <li className="breadcrumb-item">
              <Link
                to={{
                  pathname: "/search",
                  search: "?search=" + this.state.input
                }}
              >
                Search
              </Link>
            </li>
          </ol>
        </nav>
      </nav>
    );
  }
}

export default withRouter(header);
