import React, { Component } from "react";

class currentlyReading extends Component {
  //This will display the books in the currentlyReading container
  displayBooks = () => {
    return this.props.bookList.map(item => {
      if (item.shelf === "currentlyReading") {
        return (
          <div id="book" key={item.id} onClick={this.props.menuDisplay}>
            <img
              id={item.id}
              src={item.imageLinks.smallThumbnail}
              height="100"
              width="80"
              location={item.location}
              alt={item.authors}
            />
          </div>
        );
      }
    });
  };

  render() {
    return (
      <div className="wantToRead">
        <h2 className="heading">Want to Read</h2>
        <hr className="style-two" />
        <div className="wanttoreadcontainer" />
        {this.displayBooks()}
      </div>
    );
  }
}

export default currentlyReading;
