import React, { Component } from "react";
import * as BooksAPI from "../BooksAPI";

class wantToRead extends Component {
  constructor(props) {
    super(props);

    //this holds the status of display for each book
    this.state = {
      wantToReadBookList: {}
    };

    this.toggleMenu = this.toggleMenu.bind(this);
  }

  //This will get the ids of each book and associate each id with a toggle state
  componentDidUpdate = () => {
    let wantToReadBook = {}; //This is the wantToReadBook object that will hold book id and toggle state
    let key; //key to the book objects
    this.props.bookList.map(item => {
      key = item.id;
      if (item.shelf === "wantToRead") {
        wantToReadBook[key] = { id: item.id, isToggleOn: false };
        this.state.wantToReadBookList[key] = wantToReadBook[key];
      }
    });
    this.setState();
  };

  //This will toggle the menu from the state
  toggleMenu = e => {
    this.state.wantToReadBookList[e.target.id].isToggleOn = !this.state
      .wantToReadBookList[e.target.id].isToggleOn;
    this.setState();
  };

  //This creates books based on the location of the books
  displayBooks = () => {
    return this.props.bookList.map(item => {
      if (item.shelf === "wantToRead") {
        return (
          <div id={item.id} id="book">
            <img
              id={item.id}
              src={item.imageLinks.smallThumbnail}
              height="100"
              width="80"
              location={item.location}
              alt={item.authors}
              onClick={e => this.toggleMenu(e)}
            />
            <form id="locationControl" display="true">
              <button type="button">Want To Read</button>
              <button type="button">Currently Reading</button>
              <button type="button">Read</button>
              <button type="button">None</button>
            </form>
          </div>
        );
      }
    });
  };

  render = () => {
    return (
      <div className="wantToRead">
        <h2 className="heading">Want to Read</h2>
        <hr className="style-two" />
        <div className="wanttoreadcontainer" id="book" />
        {this.displayBooks()}
      </div>
    );
  };
}

export default wantToRead;
